INSERT INTO `ss_static_contents` (`id`, `title`, `content`, `create_time`, `s_locale`, `published`, `for_roles`) VALUES (1,'联系我们','<p>电话：86-111-111111</p>\r\n<p>传真：86-222-222222</p>\r\n<p>邮箱：xx@xx.com</p>\r\n<p>地址：xx省xx市xx高新区A1</p>',1290756078,'zh_CN','1','{member}{admin}{guest}'),(2,'公司简介','<p><span id=\"brief_intro\"><font size=\"3\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; xxIT科技有限公司成立2000年01月，是专业从事大型行业网站系统开发和计算机维修、打印机维修、电脑外设和打印机外设批发的专业公司。xxIT科技是一家专注网络产品开发、销售及服务等业务的高新技术企业，是网站开发技术实力最强、专业化程度最高、服务最优的计算机软硬件服务商。xxIT科技聚集了一批高素质的管理人员、一流的产品研发精英，xxIT科技向我们的客户提供一流的硬件产品及上门服务，受到全市广大人民的欢迎。　</font></span></p>',1290756078,'zh_CN','1','{guest}');
